mapper.select:
select
id,
fk_shp_shop_id as fkShpShopId,
day_count as dayCount,
fk_be_seen_shop_id as fkBeSeenShopId,
insert_time as insertTime,
insert_admin as insertAdmin
from biz_shop_see
where 1=1

	and fk_shp_shop_id = #{shopSeeSearch.shopId,jdbcType=INTEGER}


	and fk_be_seen_shop_id = #{shopSeeSearch.beSeenShopId,jdbcType=INTEGER}


	and insert_time = #{shopSeeSearch.insertTime,jdbcType=TIMESTAMP}


	and insert_admin = #{shopSeeSearch.userId,jdbcType=INTEGER}

select
sum(day_count)
from biz_shop_see
where 1=1

	and fk_be_seen_shop_id = #{shopSeeSearchByCount.shopId,jdbcType=INTEGER}


	and YEARWEEK(DATE_FORMAT(insert_time,'%Y-%m-%d'),1) = YEARWEEK(NOW());
