mapper.select:
select

FROM fin_salary_detail
WHERE fk_shp_shop_id = #{shopId}
AND fk_shp_user_id = #{userId}
AND salary_st_time = #{startDate}

select (select fs.salary_state from fin_salary fs where fs.id = fsd.fk_fin_salary_id) 'salaryState', fsd.product_attr 'proAttr', fsd.`basic_money` 'basicMoney', fsd.`else_money` 'elseMoney', fsd.`recycle_init_price_percent` 'recycleInitPricePercent', fsd.`recycle_unit_price` 'recycleUnitPrice', fsd.`recycle_profit_percent` 'recycleProfitPercent', fsd.`service_profit_percent` 'serviceProfitPercent', fsd.`scheme_type` 'schemeType', fsd.sale_result_json 'saleResultJson' FROM fin_salary_detail fsd WHERE fsd.fk_shp_shop_id = #{shopId}
AND fsd.fk_shp_user_id = #{userId}
AND fsd.salary_st_time = #{startDate}
ORDER BY fsd.insert_time DESC LIMIT 1
