mapper.select:
SELECT sa.id 'salaryId', sa.salary_state 'salaryState', sa.salary_money 'salaryMoney', sa.salary_name 'salaryName', sa.insert_time 'insertTime', us.`id` 'userId', us.`head_img_url` 'headImgUrl', (SELECT NAME FROM shp_user_type WHERE id = (SELECT fk_shp_user_type_id 'userTypeId' FROM shp_user_shop_ref uShopR WHERE uShopR.`fk_shp_shop_id` = #{shopId} AND uShopR.`fk_shp_user_id` = us.id ORDER BY state DESC LIMIT 1)) 'userTypeName', (SELECT name FROM shp_user_shop_ref uShopR WHERE uShopR.`fk_shp_shop_id` = #{shopId} AND uShopR.`fk_shp_user_id` = us.id ORDER BY state DESC LIMIT 1) 'nickname' FROM fin_salary sa LEFT JOIN shp_user us ON sa.`fk_shp_user_id` = us.`id` WHERE sa.fk_shp_shop_id = #{shopId}
AND sa.fk_shp_user_id = #{userId}
AND sa.salary_st_time >= '${startDate} 00:00:00'
AND sa.salary_et_time <= '${endDate} 23:59:59'
ORDER BY sa.`salary_money` DESC, userTypeName DESC, sa.id ASC

SELECT IFNULL(SUM(sa.salary_money),0) FROM fin_salary sa
WHERE sa.fk_shp_shop_id = #{shopId}

    AND sa.fk_shp_user_id = #{userId}


    AND sa.salary_st_time >= '${startDate} 00:00:00'


    AND sa.salary_et_time <= '${endDate} 23:59:59'

SELECT
  fk_shp_user_id 'userId'
FROM
  fin_salary
WHERE fk_shp_shop_id = #{shopId}
  AND salary_st_time = #{startDate}
  AND salary_et_time <= #{endDate}

SELECT
  id
FROM
  fin_salary
WHERE fk_shp_shop_id = #{shopId}
  AND salary_st_time BETWEEN #{startDateTime}
  AND #{endDateTime}
