mapper.select:
select op.id as id, op.problem_category_name as problemCategoryName, op.problem_title as problemTitle, op.problem_answer as problemAnswer, op.sort as sort, op.state as state, op.insert_time as insertTime, op.update_time as updateTime, op.del as del, op.type as type, op.video_url as videoUrl, op.play_num as playNum, su.nickname AS createPerson from op_problem op LEFT JOIN sys_user su ON op.insert_admin = su.id where (1=1) and op.del = '0'
and op.problem_category_name = #{problemCategoryName}
and op.problem_title like CONCAT('%',#{problemTitle},'%')
and op.state = #{state}
and op.type = #{type}
order by op.sort desc,op.insert_time desc

select op.id as id, op.problem_title as problemTitle, op.problem_answer as problemAnswer, op.state as state, op.insert_time as insertTime, op.del as del from op_problem op where (1=1)
and op.problem_category_name = #{problemCategoryName}
and op.type = #{type}
and op.del = '0' and op.state = 1 order by op.sort desc,op.insert_time desc
