mapper.select:
select count(*) from op_union_agent
where
fk_shp_user_id = #{shpUserId}
and del = 0

    and id != #{id}

SELECT op_union_agent.id AS id, op_union_agent.fk_shp_user_id AS userId, shp_user.nickname AS nickname, shp_user.username AS username, shp_user.insert_time AS insertTime, op_union_agent.insert_time AS unionAgentInsertTime, op_union_agent.agent_switch as agentSwitch, op_union_agent.valid_day AS validDay, op_union_agent.fk_op_employee_id as employeeId, (select nickname from shp_user where del = 0 and id = op_employee.fk_shp_user_id) as employeeNickname FROM op_union_agent LEFT JOIN shp_user ON op_union_agent.fk_shp_user_id = shp_user.id LEFT JOIN op_employee ON op_union_agent.fk_op_employee_id = op_employee.id and op_employee.del = 0 WHERE op_union_agent.del = 0
and shp_user.username = #{username}
and op_union_agent.fk_op_employee_id = #{employeeId}
