mapper.select:
select au.id as id ,au.fk_org_organization_id as organizationId ,au.phone as phone ,au.insert_time as insertTime ,au.insert_admin as insertAdmin ,su.nickname as insertAdminName ,au.access_type as accessType from org_access_user au left join shp_user su on au.insert_admin =su.id au.access_type>0
and au.fk_org_organization_id = #{organizationId,jdbcType=INTEGER}
and au.access_type = #{accessType,jdbcType=VARCHAR}
order by au.insert_time desc

select id ,fk_shp_shop_id as fkShpShopId ,fk_org_organization_id as fkOrgOrganizationId ,phone as phone ,access_type as accessType ,insert_time as insertTime ,update_time as updateTime ,insert_admin as insertAdmin ,update_admin as updateAdmin , remark from org_access_user au
au.access_type>0
and au.phone = #{phone,jdbcType=VARCHAR}
and au.fk_org_organization_id = #{organizationId,jdbcType=INTEGER}
limit 1
