mapper.select:
select oot.id as id ,oot.fk_org_organization_id as organizationId ,oot.fk_pro_temp_id as tempId ,oot.temp_seat_name as tempSeatName ,oot.show_seat as showSeat ,oot.insert_time as insertTime , ss.name as shopName , su.nickname as insertAdminName from org_organization_temp oot left join pro_temp pt on oot.fk_pro_temp_id =pt.id left join shp_shop ss on pt.fk_shp_shop_id =ss.id left join shp_user su on oot.insert_admin =su.id 1=1
and fk_org_organization_id = #{organizationId,jdbcType=INTEGER}
order by oot.insert_time desc

select sum(IFNULL((tp.num),pp.total_num)) from pro_temp_product tp left join pro_product pp on tp.fk_pro_product_id = pp.id
left join org_organization_temp ot on ot.fk_pro_temp_id =tp.fk_pro_temp_id
pp.fk_pro_state_code BETWEEN 10 AND 39
and ot.fk_org_organization_id = #{organizationId,jdbcType=INTEGER}
and tp.fk_pro_temp_id = #{tempId,jdbcType=INTEGER}

select count(1) from (select ot.fk_shp_shop_id from org_organization_temp ot left join pro_temp pt on pt.id=ot.fk_pro_temp_id 1=1
and ot.fk_org_organization_id = #{organizationId,jdbcType=INTEGER}
and ot.fk_shp_shop_id = #{shopId,jdbcType=INTEGER}
group by pt.fk_shp_shop_id) a

select sum(IFNULL((tp.num),pp.total_num)*(IFNULL((tp.trade_price),pp.trade_price))) from pro_temp_product tp left join pro_product pp on tp.fk_pro_product_id = pp.id left join org_organization_temp ot on ot.fk_pro_temp_id =tp.fk_pro_temp_id pp.fk_pro_state_code BETWEEN 10 AND 39
and ot.fk_org_organization_id = #{organizationId,jdbcType=INTEGER}
and tp.fk_shp_shop_id = #{shopId,jdbcType=INTEGER}
