mapper.select:
select id, fk_shp_shop_id 'fkShpShopId', fk_shp_user_id 'fkShpUserId', fk_pro_product_id 'fkProProductId', type, attribute_name 'attributeName', before_value 'beforeValue', after_value 'afterValue', insert_time 'insertTime', source, remark from pro_modify_record where fk_shp_shop_id = #{shopId} and fk_pro_product_id = #{proId}
and DATE_FORMAT(insert_time,'%Y-%m-%d') >= #{operateTimeStart}
and #{operateTimeEnd} >= DATE_FORMAT(insert_time,'%Y-%m-%d')
and (attribute_name like CONCAT('%',#{searchContentKey},'%') OR type like CONCAT('%',#{searchContentKey},'%') OR before_value like CONCAT('%',#{searchContentKey},'%') OR after_value like CONCAT('%',#{searchContentKey},'%'))
and fk_shp_user_id = #{operateUserId}
order by id desc
