mapper.select:
		select
		id,
		fk_pro_product_id,
		fk_shp_shop_id,
		watch_core_type,
		watchcase,
		watchcase_size,
		material,
		object_size,
		clothes_size,
		public_price,
		insert_time,
		update_time,
		update_admin,
		del,
		remark
		from pro_standard

		 and pro_standard.id = #{id,jdbcType=INTEGER}
		 and pro_standard.fk_pro_product_id = #{fkProProductId,jdbcType=INTEGER}
		 and pro_standard.fk_shp_shop_id = #{fkShpShopId,jdbcType=INTEGER}
		 and pro_standard.watch_core_type = #{watchCoreType,jdbcType=VARCHAR}
		 and pro_standard.watchcase = #{watchcase,jdbcType=VARCHAR}
		 and pro_standard.watchcase_size = #{watchcaseSize,jdbcType=VARCHAR}
		 and pro_standard.material = #{material,jdbcType=VARCHAR}
		 and pro_standard.object_size = #{objectSize,jdbcType=VARCHAR}
		 and pro_standard.clothes_size = #{clothesSize,jdbcType=VARCHAR}
		 and pro_standard.public_price = #{publicPrice,jdbcType=INTEGER}
		 and pro_standard.insert_time = #{insertTime,jdbcType=TIMESTAMP}
		 and pro_standard.update_time = #{updateTime,jdbcType=TIMESTAMP}
		 and pro_standard.update_admin = #{updateAdmin,jdbcType=INTEGER}
		 and pro_standard.del = #{del,jdbcType=VARCHAR}
		 and pro_standard.remark = #{remark,jdbcType=VARCHAR}



              ${orderByString}


              order by id desc

