mapper.select:
		select
		id,
		fk_shp_shop_id,
		code,
		hours,
		insert_time,
		update_time,
		insert_admin,
		update_admin,
		versions,
		remark
		from shp_alter_history

		 and shp_alter_history.id = #{id,jdbcType=INTEGER}
		 and shp_alter_history.fk_shp_shop_id = #{fkShpShopId,jdbcType=INTEGER}
		 and shp_alter_history.code = #{code,jdbcType=VARCHAR}
		 and shp_alter_history.hours = #{hours,jdbcType=DECIMAL}
		 and shp_alter_history.insert_time = #{insertTime,jdbcType=TIMESTAMP}
		 and shp_alter_history.update_time = #{updateTime,jdbcType=TIMESTAMP}
		 and shp_alter_history.insert_admin = #{insertAdmin,jdbcType=INTEGER}
		 and shp_alter_history.update_admin = #{updateAdmin,jdbcType=INTEGER}
		 and shp_alter_history.versions = #{versions,jdbcType=INTEGER}
		 and shp_alter_history.remark = #{remark,jdbcType=VARCHAR}



              ${orderByString}


              order by id desc

