mapper.select:
SELECT
  fk_shp_perm_index_id 'permId'
FROM
  shp_perm_tpl
WHERE fk_shp_shop_id = #{shopId}
  AND fk_shp_user_type_id = #{userTypeId}

mapper.delete:
DELETE
FROM
  shp_perm_tpl
WHERE fk_shp_shop_id = #{shopId}
  AND fk_shp_user_type_id = #{userTypeId}

delete from shp_perm_tpl
WHERE fk_shp_shop_id = #{shopId}
  AND fk_shp_user_type_id = #{userTypeId}
