mapper.select:
select count(id) from shp_perm_user_ref
where fk_shp_perm_index_id in (${queryPermIds})
AND fk_shp_shop_id = #{shopId}
AND fk_shp_user_id = #{userId}

mapper.delete:
DELETE
FROM
  shp_perm_user_ref
WHERE fk_shp_shop_id = #{shopId}
  AND fk_shp_user_id = #{userId}

delete from shp_perm_user_ref
where fk_shp_user_id in (${userIds})
and fk_shp_shop_id = #{shopId}

mapper.update:
update shp_perm_user_ref
set fk_shp_user_id = #{oldUserId} , insert_time = now()
where fk_shp_shop_id = #{shopId} and fk_shp_user_id = #{newUserId}
