mapper.select:
		select
		id,
		fk_shp_shop_id,
		fk_shp_share_type_code,
		fk_shp_user_id,
		total_count,
		total_hours,
		insert_time,
		update_time,
		insert_admin,
		update_admin,
		versions,
		remark
		from shp_share_total

		 and shp_share_total.id = #{id,jdbcType=INTEGER}
		 and shp_share_total.fk_shp_shop_id = #{fkShpShopId,jdbcType=INTEGER}
		 and shp_share_total.fk_shp_share_type_code = #{fkShpShareTypeCode,jdbcType=INTEGER}
		 and shp_share_total.fk_shp_user_id = #{fkShpUserId,jdbcType=INTEGER}
		 and shp_share_total.total_count = #{totalCount,jdbcType=INTEGER}
		 and shp_share_total.total_hours = #{totalHours,jdbcType=DECIMAL}
		 and shp_share_total.insert_time = #{insertTime,jdbcType=TIMESTAMP}
		 and shp_share_total.update_time = #{updateTime,jdbcType=TIMESTAMP}
		 and shp_share_total.insert_admin = #{insertAdmin,jdbcType=INTEGER}
		 and shp_share_total.update_admin = #{updateAdmin,jdbcType=INTEGER}
		 and shp_share_total.versions = #{versions,jdbcType=INTEGER}
		 and shp_share_total.remark = #{remark,jdbcType=VARCHAR}



              ${orderByString}


              order by id desc
