mapper.select:
select 
from sys_role_permission_ref
where (1=1)

  and id = #{id}


  and fk_sys_role_id = #{fkSysRoleId}


  and fk_sys_permission_id = #{fkSysPermissionId}


  and insert_time = #{insertTime}


  and update_time = #{updateTime}


  and insert_admin = #{insertAdmin}


  and update_admin = #{updateAdmin}


  and versions = #{versions}


  and del = #{del}


  and remark = #{remark}
