mapper.select:
select 
from sys_user_role_ref
where (1=1)

  and fk_sys_user_id = #{fkSysUserId,jdbcType=INTEGER}


  and fk_sys_role_id = #{fkSysRoleId,jdbcType=INTEGER}


  and insert_time = #{insertTime,jdbcType=TIMESTAMP}


  and update_time = #{updateTime,jdbcType=TIMESTAMP}


  and insert_admin = #{insertAdmin,jdbcType=INTEGER}


  and update_admin = #{updateAdmin,jdbcType=INTEGER}


  and versions = #{versions,jdbcType=INTEGER}


  and del = #{del,jdbcType=CHAR}


  and remark = #{remark,jdbcType=VARCHAR}
